def ImageToMatrix(filename):
    im = Image.open(filename)
    height,width = im.size
    im = im.convert(&amp;#39;L&amp;#39;)
    data = im.getdata()
    data = np.matrix(data,dtype=&amp;#39;float&amp;#39;)/255.0
    new_data = np.reshape(data,(width,height))
    return new_data
